pragma solidity 0.5.12;

contract TokenInterface {
    function balanceOf(address) public returns (uint);
    function transfer(address, uint) public returns (bool);
}

contract Faucet {

    event LOG_CALL(
        bytes4  indexed sig,
        address indexed caller,
        bytes           data
    ) anonymous;

    address private _owner;

    constructor () public {
        _owner = msg.sender;
    }

    modifier _logs_() {
        emit LOG_CALL(msg.sig, msg.sender, msg.data);
        _;
    }

    modifier _onlyOwner_() {
        require(msg.sender == _owner, "ERR_NOT_OWNER");
        _;
    }

    mapping (address => uint256) public amount;
    mapping (address => mapping (address => uint)) public lastDrip;

    uint public constant BONE = 10**18;

    function setAmount(address token, uint256 amount_)
        external
        _onlyOwner_
        _logs_
    {
        amount[token] = amount_;
    }

    function drip(address token)
        external
        _logs_
    {
        require(now - lastDrip[msg.sender][address(token)] >= 1 days, "ERR_DRIP_THROTTLE");
        require(TokenInterface(token).balanceOf(address(this)) >= amount[token], "ERR_NEEDS_REFILL");
        lastDrip[msg.sender][address(token)] = now;
        TokenInterface(token).transfer(msg.sender, amount[token]);
    }

    function seal(TokenInterface token)
        external
        _onlyOwner_
        _logs_
    {
        token.transfer(msg.sender, token.balanceOf(address(this)));
    }
}
